import React from "react";
import styled from "styled-components";
import { hero_images } from "../utils/images";

const Hero = () => {
    return (
        <HeroWrapper className="bg-black">
            <div className="container h-100 flex">
                <div className="hero-content">
                    <h1> Welcome to the LafargeHolcim Maroc E-learning Platform!</h1>
                    <p>  Start learning with us. Learning that comes to you. </p>
                </div>
            </div>
        </HeroWrapper>
    )
}

const HeroWrapper= styled.div`
background: url(${hero_images.hero1}) center/cover no-repeat;
  height: 300px;
  .container{
    .hero-content{
      background-color: var(--clr-white);
      max-width: 400px;
      width: 100%;
      margin-left: 0;
      padding: 20px;
      h1{
        font-size: 32px;
        margin-bottom: 5px;
        white-space: nowrap;
      }
      p{
        font-size: 15px;
      }
    }
  }
`;

export default Hero;
